import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    Grid,
    Card,
    CardMedia,
    CardContent,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    IconButton,
    AppBar,
    Toolbar,
    Tabs,
    Tab,
    Box,
    Badge,
    Fade,
    useTheme,
    useMediaQuery,
    CardActions,
    Stack
} from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon, ShoppingCart, FlashOn } from '@mui/icons-material';
import axios from 'axios';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Cart from './components/orders/customer/Cart';
import OrderHistory from './components/orders/customer/OrderHistory';
import OrderManagement from './components/orders/admin/OrderManagement';
import AdminPanelSettingsIcon from '@mui/icons-material/AdminPanelSettings';
import PersonIcon from '@mui/icons-material/Person';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import Checkout from './components/orders/customer/Checkout';

const API_URL = 'http://localhost:5001/api';

function App() {
    const [products, setProducts] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [formData, setFormData] = useState({
        name: '',
        price: '',
        image: null
    });
    const [currentTab, setCurrentTab] = useState(0);
    const [cartItemCount, setCartItemCount] = useState(0);
    const [isAdmin, setIsAdmin] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [openProductDialog, setOpenProductDialog] = useState(false);
    const navigate = useNavigate();

    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

    useEffect(() => {
        fetchProducts();
        fetchCartCount();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get(`${API_URL}/products`);
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const fetchCartCount = async () => {
        try {
            const sessionId = localStorage.getItem('sessionId');
            if (sessionId) {
                const response = await axios.get(`${API_URL}/cart/${sessionId}`);
                setCartItemCount(response.data.items?.length || 0);
            }
        } catch (error) {
            console.error('Error fetching cart count:', error);
        }
    };

    const handleOpenDialog = (product = null) => {
        if (product) {
            setEditingProduct(product);
            setFormData({
                name: product.name,
                price: product.price,
                image: null
            });
        } else {
            setEditingProduct(null);
            setFormData({
                name: '',
                price: '',
                image: null
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setEditingProduct(null);
        setFormData({
            name: '',
            price: '',
            image: null
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleImageChange = (e) => {
        setFormData(prev => ({
            ...prev,
            image: e.target.files[0]
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formDataToSend = new FormData();
        formDataToSend.append('name', formData.name);
        formDataToSend.append('price', formData.price);
        if (formData.image) {
            formDataToSend.append('image', formData.image);
        }

        try {
            if (editingProduct) {
                await axios.put(`${API_URL}/products/${editingProduct._id}`, formDataToSend, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
            } else {
                await axios.post(`${API_URL}/products`, formDataToSend, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
            }
            handleCloseDialog();
            fetchProducts();
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const handleDelete = async (productId) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                await axios.delete(`${API_URL}/products/${productId}`);
                fetchProducts();
            } catch (error) {
                console.error('Error deleting product:', error);
            }
        }
    };

    const handleTabChange = (event, newValue) => {
        setCurrentTab(newValue);
    };

    const handleAddToCart = async (productId) => {
        try {
            const sessionId = localStorage.getItem('sessionId') || generateSessionId();
            localStorage.setItem('sessionId', sessionId);

            const response = await axios.post(`${API_URL}/cart/${sessionId}/items`, {
                productId,
                quantity: 1
            });

            if (response.status === 201) {
                fetchCartCount();
                // Show success message or notification
            }
        } catch (error) {
            console.error('Error adding to cart:', error);
        }
    };

    const handleBuyNow = (product) => {
        setSelectedProduct(product);
        setOpenProductDialog(true);
    };

    const handleCloseProductDialog = () => {
        setOpenProductDialog(false);
        setSelectedProduct(null);
    };

    const handleProceedToCheckout = () => {
        handleCloseProductDialog();
        navigate('/checkout');
    };

    const generateSessionId = () => {
        return Math.random().toString(36).substring(2) + Date.now().toString(36);
    };

    const renderProductCard = (product) => (
        <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <CardMedia
                component="img"
                height="200"
                image={`${API_URL}${product.image}`}
                alt={product.name}
                sx={{ cursor: 'pointer' }}
                onClick={() => handleBuyNow(product)}
            />
            <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h5" component="div">
                    {product.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                    Price: LKR {product.price} per 100g
                </Typography>
                {isAdmin ? (
                    <Box sx={{ mt: 2 }}>
                        <IconButton
                            onClick={() => handleOpenDialog(product)}
                            color="primary"
                        >
                            <EditIcon />
                        </IconButton>
                        <IconButton
                            onClick={() => handleDelete(product._id)}
                            color="error"
                        >
                            <DeleteIcon />
                        </IconButton>
                    </Box>
                ) : (
                    <Stack direction="row" spacing={1} sx={{ mt: 2 }}>
                        <Button
                            variant="contained"
                            startIcon={<ShoppingCart />}
                            onClick={() => handleAddToCart(product._id)}
                            fullWidth
                        >
                            Add to Cart
                        </Button>
                        <Button
                            variant="contained"
                            color="secondary"
                            startIcon={<FlashOn />}
                            onClick={() => handleBuyNow(product)}
                            fullWidth
                        >
                            Buy Now
                        </Button>
                    </Stack>
                )}
            </CardContent>
        </Card>
    );

    const renderContent = () => {
        switch (currentTab) {
            case 0:
                return (
                    <Grid container spacing={3}>
                        {products.map((product) => (
                            <Grid item xs={12} sm={6} md={4} key={product._id}>
                                {renderProductCard(product)}
                            </Grid>
                        ))}
                    </Grid>
                );
            case 1:
                return <Cart onUpdateCartCount={fetchCartCount} />;
            case 2:
                return isAdmin ? <OrderManagement /> : <OrderHistory customerEmail="customer@example.com" />;
            default:
                return null;
        }
    };

    return (
        <Router>
            <Box sx={{ flexGrow: 1 }}>
                <AppBar position="static" elevation={1}>
                    <Toolbar>
                        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                            Vegetable Shop
                        </Typography>
                        <Box sx={{ display: 'flex', gap: 2 }}>
                            <Button
                                color="inherit"
                                startIcon={isAdmin ? <PersonIcon /> : <AdminPanelSettingsIcon />}
                                onClick={() => setIsAdmin(!isAdmin)}
                            >
                                {isAdmin ? 'Customer View' : 'Admin View'}
                            </Button>
                            {isAdmin && (
                                <Button
                                    color="inherit"
                                    startIcon={<AddIcon />}
                                    onClick={() => {
                                        setEditingProduct(null);
                                        setFormData({ name: '', price: '', image: null });
                                        setOpenDialog(true);
                                    }}
                                >
                                    Add Product
                                </Button>
                            )}
                        </Box>
                        <Badge badgeContent={cartItemCount} color="error">
                            <IconButton color="inherit" component={Link} to="/cart">
                                <ShoppingCartIcon />
                            </IconButton>
                        </Badge>
                    </Toolbar>
                    <Tabs
                        value={currentTab}
                        onChange={handleTabChange}
                        indicatorColor="secondary"
                        textColor="inherit"
                        variant={isMobile ? "fullWidth" : "standard"}
                        sx={{ borderBottom: 1, borderColor: 'divider' }}
                    >
                        <Tab label="Products" component={Link} to="/" />
                        <Tab label="Cart" component={Link} to="/cart" />
                        <Tab 
                            label={isAdmin ? "Order Management" : "Order History"} 
                            component={Link} 
                            to={isAdmin ? "/admin/orders" : "/orders"} 
                        />
                    </Tabs>
                </AppBar>

                <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
                    <Routes>
                        <Route path="/" element={
                            <Fade in timeout={500}>
                                <Box>
                                    {renderContent()}
                                </Box>
                            </Fade>
                        } />
                        <Route path="/cart" element={<Cart />} />
                        <Route path="/checkout" element={<Checkout />} />
                        <Route path="/orders" element={<OrderHistory customerEmail="customer@example.com" />} />
                        <Route path="/admin/orders" element={<OrderManagement />} />
                    </Routes>
                </Container>

                {/* Product Details Dialog */}
                <Dialog
                    open={openProductDialog}
                    onClose={handleCloseProductDialog}
                    maxWidth="md"
                    fullWidth
                >
                    {selectedProduct && (
                        <>
                            <DialogTitle>
                                {selectedProduct.name}
                            </DialogTitle>
                            <DialogContent>
                                <Grid container spacing={3}>
                                    <Grid item xs={12} md={6}>
                                        <img
                                            src={`${API_URL}${selectedProduct.image}`}
                                            alt={selectedProduct.name}
                                            style={{ width: '100%', maxHeight: '400px', objectFit: 'cover' }}
                                        />
                                    </Grid>
                                    <Grid item xs={12} md={6}>
                                        <Typography variant="h5" gutterBottom>
                                            {selectedProduct.name}
                                        </Typography>
                                        <Typography variant="h6" color="primary" gutterBottom>
                                            LKR {selectedProduct.price} per 100g
                                        </Typography>
                                        <Typography variant="body1" paragraph>
                                            {selectedProduct.description}
                                        </Typography>
                                        <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
                                            <Button
                                                variant="contained"
                                                startIcon={<ShoppingCart />}
                                                onClick={() => {
                                                    handleAddToCart(selectedProduct._id);
                                                    handleCloseProductDialog();
                                                }}
                                                fullWidth
                                            >
                                                Add to Cart
                                            </Button>
                                            <Button
                                                variant="contained"
                                                color="secondary"
                                                startIcon={<FlashOn />}
                                                onClick={handleProceedToCheckout}
                                                fullWidth
                                            >
                                                Buy Now
                                            </Button>
                                        </Stack>
                                    </Grid>
                                </Grid>
                            </DialogContent>
                        </>
                    )}
                </Dialog>

                <Dialog 
                    open={openDialog} 
                    onClose={() => setOpenDialog(false)}
                    maxWidth="sm"
                    fullWidth
                    TransitionComponent={Fade}
                >
                    <DialogTitle>
                        {editingProduct ? 'Edit Product' : 'Add New Product'}
                    </DialogTitle>
                    <DialogContent>
                        <Box sx={{ mt: 2 }}>
                            <TextField
                                fullWidth
                                label="Product Name"
                                value={formData.name}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                                variant="outlined"
                            />
                            <TextField
                                fullWidth
                                label="Price"
                                type="number"
                                value={formData.price}
                                onChange={handleInputChange}
                                margin="normal"
                                required
                                variant="outlined"
                            />
                            <Box sx={{ mt: 2 }}>
                                <input
                                    accept="image/*"
                                    style={{ display: 'none' }}
                                    id="image-upload"
                                    type="file"
                                    onChange={handleImageChange}
                                />
                                <label htmlFor="image-upload">
                                    <Button
                                        variant="outlined"
                                        component="span"
                                        fullWidth
                                        sx={{ mt: 1 }}
                                    >
                                        {formData.image ? 'Change Image' : 'Upload Image'}
                                    </Button>
                                </label>
                                {formData.image && (
                                    <Box sx={{ mt: 1 }}>
                                        <img
                                            src={formData.image}
                                            alt="Preview"
                                            style={{ maxWidth: '100%', maxHeight: '200px', objectFit: 'contain' }}
                                        />
                                    </Box>
                                )}
                            </Box>
                        </Box>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={() => setOpenDialog(false)}>Cancel</Button>
                        <Button 
                            onClick={handleSubmit} 
                            variant="contained" 
                            color="primary"
                            disabled={!formData.name || !formData.price}
                        >
                            {editingProduct ? 'Update' : 'Add'}
                        </Button>
                    </DialogActions>
                </Dialog>
            </Box>
        </Router>
    );
}

export default App;
