import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    List,
    ListItem,
    ListItemText,
    ListItemSecondaryAction,
    IconButton,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    Grid,
    Paper,
    Box,
    Snackbar,
    Alert,
    Card,
    CardContent,
    Divider,
    CircularProgress
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);
    const [openCheckout, setOpenCheckout] = useState(false);
    const [loading, setLoading] = useState(true);
    const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
    const [orderDetails, setOrderDetails] = useState({
        name: '',
        email: '',
        phone: '',
        address: '',
        paymentMethod: 'cash'
    });

    useEffect(() => {
        fetchCart();
    }, []);

    const fetchCart = async () => {
        try {
            const sessionId = localStorage.getItem('sessionId') || generateSessionId();
            localStorage.setItem('sessionId', sessionId);

            const response = await fetch(`http://localhost:5001/api/cart/${sessionId}`);
            const data = await response.json();
            setCartItems(data.items || []);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching cart:', error);
            setSnackbar({
                open: true,
                message: 'Error loading cart items',
                severity: 'error'
            });
            setLoading(false);
        }
    };

    const generateSessionId = () => {
        return Math.random().toString(36).substring(2) + Date.now().toString(36);
    };

    const handleQuantityChange = async (productId, newQuantity) => {
        try {
            const sessionId = localStorage.getItem('sessionId');
            const response = await fetch(
                `http://localhost:5001/api/cart/${sessionId}/items/${productId}`,
                {
                    method: 'PUT',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ quantity: newQuantity }),
                }
            );
            const data = await response.json();
            setCartItems(data.items);
            setSnackbar({
                open: true,
                message: 'Cart updated successfully',
                severity: 'success'
            });
        } catch (error) {
            console.error('Error updating quantity:', error);
            setSnackbar({
                open: true,
                message: 'Error updating cart',
                severity: 'error'
            });
        }
    };

    const handleRemoveItem = async (productId) => {
        try {
            const sessionId = localStorage.getItem('sessionId');
            const response = await fetch(
                `http://localhost:5001/api/cart/${sessionId}/items/${productId}`,
                {
                    method: 'DELETE',
                }
            );
            const data = await response.json();
            setCartItems(data.items);
            setSnackbar({
                open: true,
                message: 'Item removed from cart',
                severity: 'success'
            });
        } catch (error) {
            console.error('Error removing item:', error);
            setSnackbar({
                open: true,
                message: 'Error removing item',
                severity: 'error'
            });
        }
    };

    const handleCheckout = async () => {
        try {
            const sessionId = localStorage.getItem('sessionId');
            const response = await fetch('http://localhost:5001/api/orders/customer/create', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    sessionId,
                    customerDetails: orderDetails,
                    paymentMethod: orderDetails.paymentMethod,
                }),
            });

            if (response.ok) {
                setOpenCheckout(false);
                setCartItems([]);
                localStorage.removeItem('sessionId');
                setSnackbar({
                    open: true,
                    message: 'Order placed successfully!',
                    severity: 'success'
                });
            } else {
                throw new Error('Failed to place order');
            }
        } catch (error) {
            console.error('Error placing order:', error);
            setSnackbar({
                open: true,
                message: 'Failed to place order. Please try again.',
                severity: 'error'
            });
        }
    };

    const calculateTotal = () => {
        return cartItems.reduce((total, item) => {
            return total + (item.productId.price * item.quantity);
        }, 0);
    };

    const handleCloseSnackbar = () => {
        setSnackbar({ ...snackbar, open: false });
    };

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Container maxWidth="md">
            <Typography variant="h4" gutterBottom sx={{ mt: 4, mb: 4 }}>
                Shopping Cart
            </Typography>

            {cartItems.length === 0 ? (
                <Card>
                    <CardContent sx={{ textAlign: 'center', py: 4 }}>
                        <ShoppingCartIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
                        <Typography variant="h6" color="text.secondary">
                            Your cart is empty
                        </Typography>
                        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                            Add some products to your cart to see them here
                        </Typography>
                    </CardContent>
                </Card>
            ) : (
                <>
                    <List>
                        {cartItems.map((item) => (
                            <Paper key={item.productId._id} sx={{ mb: 2, p: 2, borderRadius: 2 }}>
                                <ListItem>
                                    <ListItemText
                                        primary={
                                            <Typography variant="h6" component="div">
                                                {item.productId.name}
                                            </Typography>
                                        }
                                        secondary={
                                            <Typography variant="body1" color="primary">
                                                ${item.productId.price.toFixed(2)}
                                            </Typography>
                                        }
                                    />
                                    <Box sx={{ display: 'flex', alignItems: 'center', mr: 2 }}>
                                        <IconButton
                                            onClick={() => handleQuantityChange(item.productId._id, item.quantity - 1)}
                                            disabled={item.quantity <= 1}
                                            color="primary"
                                        >
                                            <RemoveIcon />
                                        </IconButton>
                                        <Typography sx={{ mx: 2, minWidth: 30, textAlign: 'center' }}>
                                            {item.quantity}
                                        </Typography>
                                        <IconButton
                                            onClick={() => handleQuantityChange(item.productId._id, item.quantity + 1)}
                                            color="primary"
                                        >
                                            <AddIcon />
                                        </IconButton>
                                    </Box>
                                    <ListItemSecondaryAction>
                                        <IconButton
                                            edge="end"
                                            onClick={() => handleRemoveItem(item.productId._id)}
                                            color="error"
                                        >
                                            <DeleteIcon />
                                        </IconButton>
                                    </ListItemSecondaryAction>
                                </ListItem>
                            </Paper>
                        ))}
                    </List>

                    <Card sx={{ mt: 4, mb: 4 }}>
                        <CardContent>
                            <Grid container spacing={2}>
                                <Grid item xs={12}>
                                    <Divider sx={{ mb: 2 }} />
                                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                        <Typography variant="h6">
                                            Total Amount:
                                        </Typography>
                                        <Typography variant="h5" color="primary">
                                            ${calculateTotal().toFixed(2)}
                                        </Typography>
                                    </Box>
                                </Grid>
                                <Grid item xs={12}>
                                    <Button
                                        variant="contained"
                                        color="primary"
                                        size="large"
                                        fullWidth
                                        onClick={() => setOpenCheckout(true)}
                                    >
                                        Proceed to Checkout
                                    </Button>
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>

                    <Dialog 
                        open={openCheckout} 
                        onClose={() => setOpenCheckout(false)}
                        maxWidth="sm"
                        fullWidth
                    >
                        <DialogTitle>Checkout</DialogTitle>
                        <DialogContent>
                            <Grid container spacing={2} sx={{ mt: 1 }}>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Name"
                                        value={orderDetails.name}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, name: e.target.value })}
                                        required
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Email"
                                        type="email"
                                        value={orderDetails.email}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, email: e.target.value })}
                                        required
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Phone"
                                        value={orderDetails.phone}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, phone: e.target.value })}
                                        required
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Delivery Address"
                                        multiline
                                        rows={3}
                                        value={orderDetails.address}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, address: e.target.value })}
                                        required
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        select
                                        label="Payment Method"
                                        value={orderDetails.paymentMethod}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, paymentMethod: e.target.value })}
                                        required
                                        variant="outlined"
                                    >
                                        <option value="cash">Cash on Delivery</option>
                                        <option value="card">Credit/Debit Card</option>
                                    </TextField>
                                </Grid>
                            </Grid>
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={() => setOpenCheckout(false)}>Cancel</Button>
                            <Button 
                                onClick={handleCheckout} 
                                variant="contained" 
                                color="primary"
                                disabled={!orderDetails.name || !orderDetails.email || !orderDetails.phone || !orderDetails.address}
                            >
                                Place Order
                            </Button>
                        </DialogActions>
                    </Dialog>

                    <Snackbar 
                        open={snackbar.open} 
                        autoHideDuration={6000} 
                        onClose={handleCloseSnackbar}
                        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
                    >
                        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
                            {snackbar.message}
                        </Alert>
                    </Snackbar>
                </>
            )}
        </Container>
    );
};

export default Cart;
