import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    List,
    ListItem,
    ListItemText,
    ListItemSecondaryAction,
    IconButton,
    Button,
    TextField,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    Grid,
    Box
} from '@mui/material';
import { Delete as DeleteIcon, Edit as EditIcon } from '@mui/icons-material';
import axios from 'axios';

const API_URL = 'http://localhost:5001/api';

function Cart() {
    const [cart, setCart] = useState({ items: [] });
    const [openCheckout, setOpenCheckout] = useState(false);
    const [orderDetails, setOrderDetails] = useState({
        customerName: '',
        customerEmail: '',
        customerPhone: '',
        deliveryAddress: {
            street: '',
            city: '',
            state: '',
            zipCode: '',
            country: ''
        }
    });

    useEffect(() => {
        fetchCart();
    }, []);

    const fetchCart = async () => {
        try {
            const sessionId = localStorage.getItem('sessionId') || 'default-session';
            const response = await axios.get(`${API_URL}/cart/${sessionId}`);
            setCart(response.data);
        } catch (error) {
            console.error('Error fetching cart:', error);
        }
    };

    const handleQuantityChange = async (productId, newQuantity) => {
        try {
            const sessionId = localStorage.getItem('sessionId') || 'default-session';
            await axios.put(`${API_URL}/cart/${sessionId}/items/${productId}`, {
                quantity: newQuantity
            });
            fetchCart();
        } catch (error) {
            console.error('Error updating quantity:', error);
        }
    };

    const handleRemoveItem = async (productId) => {
        try {
            const sessionId = localStorage.getItem('sessionId') || 'default-session';
            await axios.delete(`${API_URL}/cart/${sessionId}/items/${productId}`);
            fetchCart();
        } catch (error) {
            console.error('Error removing item:', error);
        }
    };

    const handleCheckout = async () => {
        try {
            const sessionId = localStorage.getItem('sessionId') || 'default-session';
            await axios.post(`${API_URL}/orders`, {
                sessionId,
                ...orderDetails
            });
            setOpenCheckout(false);
            fetchCart();
        } catch (error) {
            console.error('Error creating order:', error);
        }
    };

    const calculateTotal = () => {
        return cart.items.reduce((total, item) => {
            return total + (item.product.price * item.quantity);
        }, 0);
    };

    return (
        <Container maxWidth="md" sx={{ mt: 4 }}>
            <Typography variant="h4" gutterBottom>
                Shopping Cart
            </Typography>

            {cart.items.length === 0 ? (
                <Typography>Your cart is empty</Typography>
            ) : (
                <>
                    <List>
                        {cart.items.map((item) => (
                            <ListItem key={item.product._id}>
                                <ListItemText
                                    primary={item.product.name}
                                    secondary={`Price: LKR ${item.product.price} per 100g`}
                                />
                                <Box sx={{ display: 'flex', alignItems: 'center', mr: 2 }}>
                                    <TextField
                                        type="number"
                                        value={item.quantity}
                                        onChange={(e) => handleQuantityChange(item.product._id, parseInt(e.target.value))}
                                        inputProps={{ min: 1 }}
                                        size="small"
                                        sx={{ width: 80 }}
                                    />
                                </Box>
                                <ListItemSecondaryAction>
                                    <IconButton edge="end" onClick={() => handleRemoveItem(item.product._id)}>
                                        <DeleteIcon />
                                    </IconButton>
                                </ListItemSecondaryAction>
                            </ListItem>
                        ))}
                    </List>

                    <Box sx={{ mt: 3, textAlign: 'right' }}>
                        <Typography variant="h6" gutterBottom>
                            Total: LKR {calculateTotal()}
                        </Typography>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={() => setOpenCheckout(true)}
                        >
                            Proceed to Checkout
                        </Button>
                    </Box>

                    <Dialog open={openCheckout} onClose={() => setOpenCheckout(false)}>
                        <DialogTitle>Checkout</DialogTitle>
                        <DialogContent>
                            <Grid container spacing={2} sx={{ mt: 1 }}>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Name"
                                        value={orderDetails.customerName}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, customerName: e.target.value })}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Email"
                                        type="email"
                                        value={orderDetails.customerEmail}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, customerEmail: e.target.value })}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Phone"
                                        value={orderDetails.customerPhone}
                                        onChange={(e) => setOrderDetails({ ...orderDetails, customerPhone: e.target.value })}
                                    />
                                </Grid>
                                <Grid item xs={12}>
                                    <TextField
                                        fullWidth
                                        label="Street Address"
                                        value={orderDetails.deliveryAddress.street}
                                        onChange={(e) => setOrderDetails({
                                            ...orderDetails,
                                            deliveryAddress: { ...orderDetails.deliveryAddress, street: e.target.value }
                                        })}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="City"
                                        value={orderDetails.deliveryAddress.city}
                                        onChange={(e) => setOrderDetails({
                                            ...orderDetails,
                                            deliveryAddress: { ...orderDetails.deliveryAddress, city: e.target.value }
                                        })}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="State"
                                        value={orderDetails.deliveryAddress.state}
                                        onChange={(e) => setOrderDetails({
                                            ...orderDetails,
                                            deliveryAddress: { ...orderDetails.deliveryAddress, state: e.target.value }
                                        })}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="ZIP Code"
                                        value={orderDetails.deliveryAddress.zipCode}
                                        onChange={(e) => setOrderDetails({
                                            ...orderDetails,
                                            deliveryAddress: { ...orderDetails.deliveryAddress, zipCode: e.target.value }
                                        })}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Country"
                                        value={orderDetails.deliveryAddress.country}
                                        onChange={(e) => setOrderDetails({
                                            ...orderDetails,
                                            deliveryAddress: { ...orderDetails.deliveryAddress, country: e.target.value }
                                        })}
                                    />
                                </Grid>
                            </Grid>
                        </DialogContent>
                        <DialogActions>
                            <Button onClick={() => setOpenCheckout(false)}>Cancel</Button>
                            <Button onClick={handleCheckout} variant="contained" color="primary">
                                Place Order
                            </Button>
                        </DialogActions>
                    </Dialog>
                </>
            )}
        </Container>
    );
}

export default Cart;
