import React, { useState, useEffect } from 'react';
import {
    Container,
    Typography,
    Grid,
    Card,
    CardMedia,
    CardContent,
    CardActions,
    Button,
    Dialog,
    DialogTitle,
    DialogContent,
    DialogActions,
    TextField,
    IconButton,
    AppBar,
    Toolbar,
    Tabs,
    Tab,
    Box,
    Badge
} from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import axios from 'axios';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import Cart from './components/orders/customer/Cart';
import OrderHistory from './components/orders/customer/OrderHistory';
import OrderManagement from './components/orders/admin/OrderManagement';

const API_URL = 'http://localhost:5001/api';

function App() {
    const [products, setProducts] = useState([]);
    const [openDialog, setOpenDialog] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [formData, setFormData] = useState({
        name: '',
        price: '',
        image: null
    });
    const [currentTab, setCurrentTab] = useState(0);
    const [cartItemCount, setCartItemCount] = useState(0);
    const [isAdmin, setIsAdmin] = useState(false);

    useEffect(() => {
        fetchProducts();
        fetchCartCount();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get(`${API_URL}/products`);
            setProducts(response.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const fetchCartCount = async () => {
        try {
            const sessionId = localStorage.getItem('sessionId');
            if (sessionId) {
                const response = await axios.get(`${API_URL}/cart/${sessionId}`);
                setCartItemCount(response.data.items?.length || 0);
            }
        } catch (error) {
            console.error('Error fetching cart count:', error);
        }
    };

    const handleOpenDialog = (product = null) => {
        if (product) {
            setEditingProduct(product);
            setFormData({
                name: product.name,
                price: product.price,
                image: null
            });
        } else {
            setEditingProduct(null);
            setFormData({
                name: '',
                price: '',
                image: null
            });
        }
        setOpenDialog(true);
    };

    const handleCloseDialog = () => {
        setOpenDialog(false);
        setEditingProduct(null);
        setFormData({
            name: '',
            price: '',
            image: null
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleImageChange = (e) => {
        setFormData(prev => ({
            ...prev,
            image: e.target.files[0]
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formDataToSend = new FormData();
        formDataToSend.append('name', formData.name);
        formDataToSend.append('price', formData.price);
        if (formData.image) {
            formDataToSend.append('image', formData.image);
        }

        try {
            if (editingProduct) {
                await axios.put(`${API_URL}/products/${editingProduct._id}`, formDataToSend, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
            } else {
                await axios.post(`${API_URL}/products`, formDataToSend, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
            }
            handleCloseDialog();
            fetchProducts();
        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    const handleDelete = async (productId) => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                await axios.delete(`${API_URL}/products/${productId}`);
                fetchProducts();
            } catch (error) {
                console.error('Error deleting product:', error);
            }
        }
    };

    const handleTabChange = (event, newValue) => {
        setCurrentTab(newValue);
    };

    const renderContent = () => {
        switch (currentTab) {
            case 0:
                return (
                    <Grid container spacing={3}>
                        {products.map((product) => (
                            <Grid item xs={12} sm={6} md={4} key={product._id}>
                                <Card>
                                    <CardMedia
                                        component="img"
                                        height="200"
                                        image={`${API_URL}${product.image}`}
                                        alt={product.name}
                                    />
                                    <CardContent>
                                        <Typography gutterBottom variant="h5" component="div">
                                            {product.name}
                                        </Typography>
                                        <Typography variant="body2" color="text.secondary">
                                            Price: LKR {product.price} per 100g
                                        </Typography>
                                        {isAdmin && (
                                            <Box sx={{ mt: 2 }}>
                                                <IconButton
                                                    onClick={() => handleOpenDialog(product)}
                                                    color="primary"
                                                >
                                                    <EditIcon />
                                                </IconButton>
                                                <IconButton
                                                    onClick={() => handleDelete(product._id)}
                                                    color="error"
                                                >
                                                    <DeleteIcon />
                                                </IconButton>
                                            </Box>
                                        )}
                                    </CardContent>
                                </Card>
                            </Grid>
                        ))}
                    </Grid>
                );
            case 1:
                return <Cart onUpdateCartCount={fetchCartCount} />;
            case 2:
                return isAdmin ? <OrderManagement /> : <OrderHistory customerEmail="customer@example.com" />;
            default:
                return null;
        }
    };

    return (
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                        Vegetable Shop
                    </Typography>
                    {isAdmin && (
                        <Button color="inherit" onClick={() => handleOpenDialog()}>
                            Add Product
                        </Button>
                    )}
                    <Button color="inherit" onClick={() => setIsAdmin(!isAdmin)}>
                        {isAdmin ? 'Switch to Customer' : 'Switch to Admin'}
                    </Button>
                </Toolbar>
                <Tabs value={currentTab} onChange={handleTabChange}>
                    <Tab label="Products" />
                    <Tab
                        label="Cart"
                        icon={
                            <Badge badgeContent={cartItemCount} color="error">
                                <ShoppingCartIcon />
                            </Badge>
                        }
                    />
                    <Tab label={isAdmin ? "Order Management" : "Order History"} />
                </Tabs>
            </AppBar>

            <Box sx={{ mt: 3 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                    <Typography variant="h4">Products</Typography>
                    <Button
                        variant="contained"
                        color="primary"
                        startIcon={<AddIcon />}
                        onClick={() => handleOpenDialog()}
                    >
                        Add Product
                    </Button>
                </Box>

                {renderContent()}
            </Box>

            <Dialog open={openDialog} onClose={handleCloseDialog}>
                <DialogTitle>
                    {editingProduct ? 'Edit Product' : 'Add New Product'}
                </DialogTitle>
                <DialogContent>
                    <TextField
                        autoFocus
                        margin="dense"
                        name="name"
                        label="Product Name"
                        type="text"
                        fullWidth
                        value={formData.name}
                        onChange={handleInputChange}
                    />
                    <TextField
                        margin="dense"
                        name="price"
                        label="Price (LKR per 100g)"
                        type="number"
                        fullWidth
                        value={formData.price}
                        onChange={handleInputChange}
                    />
                    <input
                        accept="image/*"
                        style={{ display: 'none' }}
                        id="image-upload"
                        type="file"
                        onChange={handleImageChange}
                    />
                    <label htmlFor="image-upload">
                        <Button
                            component="span"
                            variant="outlined"
                            sx={{ mt: 2 }}
                        >
                            Upload Image
                        </Button>
                        {formData.image && (
                            <Typography variant="body2" sx={{ mt: 1 }}>
                                Selected: {formData.image.name}
                            </Typography>
                        )}
                    </label>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseDialog}>Cancel</Button>
                    <Button onClick={handleSubmit} variant="contained" color="primary">
                        {editingProduct ? 'Update' : 'Add'}
                    </Button>
                </DialogActions>
            </Dialog>
        </Container>
    );
}

export default App;
